package com.Loops;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
	  for(int i=2;i<5;i++)
	  {
		  System.out.println(i+"hi");
	  }
		  for(int i=1;i<=2;i++)
		  {
			  System.out.println(i+"webdriver");
	      }
		  for(int i=-2;i>=-5;i--)
		  {
			  System.out.println(i+"selenium");
		  }
		  
		  
		  
		  
		  
		  
		  
		  
		  
	}
			
	}
		
			
		
			
			
			
			
			
			

	


