package com.Variables;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//syntax: data type variable = value
		
		int intvariable=30;
		
		System.out.println("The value of the variable int variable is:-"+intvariable);
		
		byte  bytevariable = 70;
		
		System.out.println("The value of the variable bytevariable is:-"+bytevariable);
		
		short hello=90;
		
		System.out.println("The value of the variable hello is:-"+hello);
		
		long alekhya=100;	
		
		System.out.println("The value of the variable alekhya is:-"+alekhya);
		
		double doublevariable= 40.33;
		System.out.println("The value of the variable doublevariable is:-"+doublevariable);
		
		double variable1=89.888888888789999;

		System.out.println("The value of the variable variable1 is:-"+variable1);

        float floatvariable=67.123546f;
		System.out.println("The value of the variable floatvariable is:-"+floatvariable);
		
		String stringvariable="ab";
		System.out.println("The value of the variable stringvariable is:-"+stringvariable);
		
		char charvariable='b';
		System.out.println("The value of the variable charvariable is:-"+charvariable);



	}

}
